import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { Character } from '../../interfaces/character.interface';

@Component({
  selector: 'app-dbz-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {

  @Input()
  public characterList: Character[] = [{
    name: 'Trunks',
    power: 10
  }]

  @Output()
  public emitIndex: EventEmitter<Number> = new EventEmitter();


  onDelete(index:number):number{
    return index;
  }
  onDeleteCharacter(index:number):void{
    //Emitir el ID del personaje
    console.log({index});
  }
}
<h4>Listado</h4>
<ul class="list-group">
  <li *ngFor="
    let character of characterList;
    let i = index;
    let isFirst = first;
    let isLast = last;
    let isEven = even;
    let isOdd = odd;
  "
  [class]="'list-group-item d-flex justify-content-between align-items-center'"
  [ngClass]="{ 'list-group-item-success': isLast,
                'list-group-item-primary': isEven
              }">
  <div>
    <span class="text-primary">{{ i + 1 }} </span>
    <span>{{ character.name }} - </span>
    <strong>Power: </strong>
    <span>{{ character.power }}</span>

    <span>Es Par: {{ isEven }}</span>
    <span>Es Impar: {{ isOdd }}</span>
  </div>
    <button (click)="onDeleteCharacter(i)" class="btn btn-danger">x</button>
    <button (onDelete)="onDeleteCharacter(i)" class="btn btn-danger">Eliminar</button>
  </li>
</ul>
