import { Component } from '@angular/core';

@Component({
  selector: 'app-heroes-list',
  templateUrl: './list.component.html',
  styleUrls: ['./list.component.css']
})
export class ListComponent {

  public heroNames: string[] = ['Spiderman', 'Ironman', 'Hulk', 'She Hulk', 'Thor']
  public deletedHero?: string;

  removeLastHero():void{
    this.deletedHero = this.heroNames.pop();
  }


}
<h3>Listado de Héroes</h3>

<div *ngIf="deletedHero">
  <h3>Héroe borrado <small class="text-danger">{{ deletedHero }}</small></h3>
</div>
<div>

</div>
<h3>No ha borrado nada.</h3>

<button (click)="removeLastHero()" class="btn btn-outline-danger">
  Borrar último héroe
</button>


<ul class="mt-2 list-group">
  <li *ngFor="let name of heroNames"
    class="list-group-item">
    {{ name }}
  </li>
</ul>
