import { Component, OnInit } from '@angular/core';
import { Character } from '../interfaces/character.interface';
import { DbzService } from '../services/dbz.service';

@Component({
  selector: 'app-dbz-main-page',
  templateUrl: './main-page.component.html'
})

export class MainPageComponent{

  constructor( public dbzService: DbzService ){
  }
}
<h1>DBZ Personajes</h1>
<hr>

<div class="row">
   <div class="col">
      <!-- Mandando input-->
      <app-dbz-list [characterList]="dbzService.characters"
                    (onDelete)="dbzService.onDeleteCharacter($event)"></app-dbz-list>
   </div>

   <div class="col">
      <!-- Mandando output-->
      <app-dbz-add-character (onNewCharacter)="onNewCharacter($event)"></app-dbz-add-character>
   </div>
</div>
